import React from "react";
import { Link, NavLink } from "react-router-dom"; // NavLink, aktif linki stilendirmek için daha iyidir
import "./Navbar.css"; // Stil dosyasını birazdan oluşturacağız

function Navbar() {
  // Kullanıcı durumunu, sayfa her render olduğunda localStorage'dan alıyoruz.
  const user = JSON.parse(localStorage.getItem("user"));
  const isAuthenticated = !!localStorage.getItem("authToken");
  const isAdmin = user?.is_admin; // Bizim API'mız 'is_admin' kullanıyor.

  // Çıkış yapma fonksiyonu
  const handleLogout = () => {
    // Backend'e haber vermek opsiyoneldir ama iyi bir pratiktir.
    // Hata alsa bile frontend'den çıkış yapılır.
    const token = localStorage.getItem("authToken");
    fetch(`${process.env.REACT_APP_API_URL}/auth/logout`, {
      method: "POST",
      headers: { Authorization: `Bearer ${token}` },
    }).catch((err) => console.error("Logout API hatası:", err));

    // En önemli kısım: Tarayıcı hafızasını temizle
    localStorage.removeItem("authToken");
    localStorage.removeItem("user");

    // Sayfanın tamamen yenilenerek state'in sıfırlanmasını sağla
    window.location.href = "/login";
  };

  return (
    <nav className="navbar">
      <div className="navbar-container">
        <Link to="/" className="navbar-brand">
          {/* Logoları public klasörüne koyduğunuzu varsayıyoruz */}
          <img src="/logo1.png" alt="Belediye Logosu" className="navbar-logo" />
          <img src="/logo2.png" alt="Kurs Logosu" className="navbar-logo" />
          <span className="navbar-title">BELEDİYE KURS KAYIT SİSTEMİ</span>
        </Link>

        <ul className="nav-menu">
          <li className="nav-item">
            {/* NavLink, aktif olduğunda 'active' sınıfını alır */}
            <NavLink to="/courses" className="nav-link">
              Tüm Kurslar
            </NavLink>
          </li>

          {isAuthenticated ? (
            // --- GİRİŞ YAPMIŞ KULLANICI ---
            <>
              {isAdmin ? (
                // Admin ise
                <li className="nav-item">
                  <NavLink to="/admin" className="nav-link">
                    Admin Paneli
                  </NavLink>
                </li>
              ) : (
                // Normal kullanıcı ise
                <li className="nav-item">
                  <NavLink to="/my-courses" className="nav-link">
                    Kurslarım
                  </NavLink>
                </li>
              )}
              <li className="nav-item">
                <button onClick={handleLogout} className="nav-button-logout">
                  Çıkış Yap
                </button>
              </li>
            </>
          ) : (
            // --- MİSAFİR KULLANICI ---
            <>
              <li className="nav-item">
                <NavLink to="/login" className="nav-link">
                  Giriş Yap
                </NavLink>
              </li>
              <li className="nav-item">
                <NavLink to="/register" className="nav-link special-link">
                  Kayıt Ol
                </NavLink>
              </li>
            </>
          )}
        </ul>
      </div>
    </nav>
  );
}

export default Navbar;
