import React, { useState, useEffect } from 'react';
import { Routes, Route, Navigate } from 'react-router-dom';
import Navbar from './Navbar';
import Home from './pages/Home.jsx';
import AdminPanel from './pages/AdminPanel.jsx';
import LoginForm from './components/Auth/LoginForm.jsx';
import RegisterForm from './components/Auth/RegisterForm.jsx';
import MyCourses from './components/Courses/MyCourses.jsx';
import './App.css';

// Oturum verisini saklamak ve paylaşmak için Context oluşturuyoruz.
export const AuthContext = React.createContext(null);

function App() {
  const [currentUser, setCurrentUser] = useState(null);

  // Sayfa yenilendiğinde localStorage'dan kullanıcıyı kontrol et
  useEffect(() => {
    const storedUser = localStorage.getItem('currentUser');
    if (storedUser) {
      setCurrentUser(JSON.parse(storedUser));
    }
  }, []);

  // Kullanıcı giriş yaptığında localStorage'a kaydet
  const handleLogin = (user) => {
    setCurrentUser(user);
    localStorage.setItem('currentUser', JSON.stringify(user));
  };

  // Kullanıcı çıkış yaptığında localStorage'dan sil
  const handleLogout = () => {
    setCurrentUser(null);
    localStorage.removeItem('currentUser');
  };

  return (
    <AuthContext.Provider value={{ currentUser, login: handleLogin, logout: handleLogout }}>
      <div className="App">
        <Navbar />
        <main className="container">
          <Routes>
            <Route path="/" element={<Home />} />
            <Route path="/login" element={<LoginForm />} />
            <Route path="/register" element={<RegisterForm />} />

            {/* Sadece giriş yapmış kullanıcıların görebileceği sayfalar */}
            <Route 
              path="/kurslarim" 
              element={currentUser ? <MyCourses /> : <Navigate to="/login" />} 
            />

            {/* Sadece admin rolündeki kullanıcıların görebileceği sayfa */}
            <Route 
              path="/admin" 
              element={currentUser && currentUser.role === 'admin' ? <AdminPanel /> : <Navigate to="/" />} 
            />
          </Routes>
        </main>
      </div>
    </AuthContext.Provider>
  );
}

export default App;
