import React, { useState, useEffect } from 'react';
import { Routes, Route, Navigate } from 'react-router-dom';
import Navbar from './Navbar';
import Home from './pages/Home.jsx';
import AdminPanel from './pages/AdminPanel.jsx';
import LoginForm from './components/Auth/LoginForm.jsx';
import RegisterForm from './components/Auth/RegisterForm.jsx';
import MyCourses from './components/Courses/MyCourses.jsx';
import './App.css';

export const AuthContext = React.createContext(null);

function App() {
  const [currentUser, setCurrentUser] = useState(null);

  useEffect(() => {
    const storedUser = localStorage.getItem('currentUser');
    if (storedUser) {
      setCurrentUser(JSON.parse(storedUser));
    }
  }, []);

  const handleLogin = (user) => {
    setCurrentUser(user);
    localStorage.setItem('currentUser', JSON.stringify(user));
  };

  const handleLogout = () => {
    setCurrentUser(null);
    localStorage.removeItem('currentUser');
  };

  // YENİ FONKSİYON: Kullanıcı verisi değiştiğinde state'i ve localStorage'ı günceller
  const updateUser = (updatedUserData) => {
    setCurrentUser(updatedUserData);
    localStorage.setItem('currentUser', JSON.stringify(updatedUserData));
  };

  // Context'e yeni fonksiyonu ekliyoruz: value={{ ..., updateUser }}
  return (
    <AuthContext.Provider value={{ currentUser, login: handleLogin, logout: handleLogout, updateUser }}>
      <div className="App">
        <Navbar />
        <main className="container">
          <Routes>
            <Route path="/" element={<Home />} />
            <Route path="/login" element={<LoginForm />} />
            <Route path="/register" element={<RegisterForm />} />

            <Route 
              path="/kurslarim" 
              element={currentUser ? <MyCourses /> : <Navigate to="/login" />} 
            />

            <Route 
              path="/admin" 
              element={currentUser && currentUser.role === 'admin' ? <AdminPanel /> : <Navigate to="/" />} 
            />
          </Routes>
        </main>
      </div>
    </AuthContext.Provider>
  );
}

export default App;
