import React, { useEffect, useState } from 'react';
import axios from 'axios';

const AdminPanel = () => {
  const [courses, setCourses] = useState([]);
  const [users, setUsers] = useState([]);
  const [newCourse, setNewCourse] = useState('');

  useEffect(() => {
    fetchCourses();
    fetchUsers();
  }, []);

  const fetchCourses = async () => {
    try {
      const res = await axios.get('http://localhost:8000/api/courses');
      setCourses(res.data);
    } catch (err) {
      console.error('Kurslar alınamadı:', err);
    }
  };

  const fetchUsers = async () => {
    try {
      const res = await axios.get('http://localhost:8000/api/users');
      setUsers(res.data);
    } catch (err) {
      console.error('Kullanıcılar alınamadı:', err);
    }
  };

  const addCourse = async () => {
    try {
      const res = await axios.post('http://localhost:8000/api/courses', {
        name: newCourse,
      });
      setNewCourse('');
      fetchCourses();
    } catch (err) {
      console.error('Kurs eklenemedi:', err);
    }
  };

  const deleteCourse = async (id) => {
    try {
      await axios.delete(`http://localhost:8000/api/courses/${id}`);
      fetchCourses();
    } catch (err) {
      console.error('Kurs silinemedi:', err);
    }
  };

  return (
    <div>
      <h2>Yönetici Paneli</h2>

      <section>
        <h3>Yeni Kurs Ekle</h3>
        <input
          type="text"
          value={newCourse}
          onChange={(e) => setNewCourse(e.target.value)}
          placeholder="Kurs Adı"
        />
        <button onClick={addCourse}>Ekle</button>
      </section>

      <section>
        <h3>Mevcut Kurslar</h3>
        <ul>
          {courses.map(course => (
            <li key={course.id}>
              {course.name} <button onClick={() => deleteCourse(course.id)}>Sil</button>
            </li>
          ))}
        </ul>
      </section>

      <section>
        <h3>Tüm Kullanıcılar</h3>
        <ul>
          {users.map(user => (
            <li key={user.id}>
              {user.username} ({user.email})
            </li>
          ))}
        </ul>
      </section>
    </div>
  );
};

export default AdminPanel;
