import React from "react";
import { BrowserRouter, Routes, Route, Navigate } from "react-router-dom";

// Bileşenleri ve Sayfaları Import Et
import Navbar from "./Navbar";
import Home from "./pages/Home.jsx";
import LoginForm from "./pages/Auth/LoginForm.jsx";
import RegisterForm from "./pages/Auth/RegisterForm.jsx";
import MyCourses from "./pages/Courses/MyCourses.jsx";
import CourseList from "./pages/Courses/CourseList.jsx"; // Bütün kursların listelendiği sayfa
import CourseDetail from "./pages/Courses/CourseDetail.jsx"; // Tek bir kursun detay sayfası
import DashboardPage from "./pages/DashboardPage.jsx"; // Normal kullanıcının paneli

import "./App.css";

/**
 * Korumalı Rota Bileşeni
 * Bir rotanın sadece giriş yapmış kullanıcılar tarafından erişilebilir olmasını sağlar.
 */
const PrivateRoute = ({ children }) => {
  const isAuthenticated = !!localStorage.getItem("authToken");
  return isAuthenticated ? children : <Navigate to="/login" />;
};

/**
 * Admin Korumalı Rota Bileşeni
 * Bir rotanın sadece admin olan kullanıcılar tarafından erişilebilir olmasını sağlar.
 */
const AdminRoute = ({ children }) => {
  const isAuthenticated = !!localStorage.getItem("authToken");
  const user = JSON.parse(localStorage.getItem("user"));
  const isAdmin = user?.is_admin;

  // Giriş yapmış ve admin ise içeriği göster, değilse ana sayfaya yönlendir.
  return isAuthenticated && isAdmin ? children : <Navigate to="/" />;
};

function App() {
  // Global state yönetimi yerine, her render'da localStorage'ı kontrol eden
  // basit ve etkili bir yapı kullanıyoruz.

  return (
    <BrowserRouter>
      <div className="App">
        {/* Navbar, her sayfada görünecek */}
        <Navbar />

        <main className="container">
          <Routes>
            {/* Herkese Açık Rotalar */}
            <Route path="/" element={<Home />} />
            <Route path="/login" element={<LoginForm />} />
            <Route path="/register" element={<RegisterForm />} />
            <Route path="/courses" element={<CourseList />} />
            <Route path="/course/:courseId" element={<CourseDetail />} />

            {/* Sadece Giriş Yapmış Kullanıcıların Erişebileceği Rotalar */}
            <Route
              path="/my-courses"
              element={
                <PrivateRoute>
                  <MyCourses />
                </PrivateRoute>
              }
            />
            <Route
              path="/dashboard"
              element={
                <PrivateRoute>
                  <DashboardPage />
                </PrivateRoute>
              }
            />

            {/* Sadece Adminlerin Erişebileceği Rotalar */}
            <Route
              path="/admin"
              element={
                <AdminRoute>
                  <AdminPanel />
                </AdminRoute>
              }
            />

            {/* Eşleşmeyen tüm diğer yolları ana sayfaya yönlendir */}
            <Route path="*" element={<Navigate to="/" />} />
          </Routes>
        </main>
      </div>
    </BrowserRouter>
  );
}

export default App;
