import React from "react";
import { Link, useNavigate } from "react-router-dom";

const Navbar = ({ username, onLogout }) => {
  const navigate = useNavigate();

  const cikisYap = () => {
    onLogout();
    navigate("/giris");
  };

  return (
    <nav style={styles.nav}>
      <div style={styles.logo}>
        <Link to="/" style={styles.link}>Kurs PortalÄ±</Link>
      </div>
      <div style={styles.menu}>
        <Link to="/kurslar" style={styles.link}>Kurslar</Link>
        <Link to="/kayitli-kurslar" style={styles.link}>KayÄ±tlarÄ±m</Link>
        {username && <span style={styles.user}>ðŸ‘¤ {username}</span>}
        <button onClick={cikisYap} style={styles.logout}>Ã‡Ä±kÄ±ÅŸ Yap</button>
      </div>
    </nav>
  );
};

const styles = {
  nav: {
