// src/components/Courses/MyCourses.jsx

import React, { useState, useEffect, useContext } from 'react';
import { api } from '../../services/api'; // api.js kullanıyorsanız
import { AuthContext } from '../../App';

function MyCourses() {
  const [myCourses, setMyCourses] = useState([]);
  const [loading, setLoading] = useState(true);
  const { currentUser } = useContext(AuthContext);

  useEffect(() => {
    // FONKSİYON DOĞRUDAN İÇERİYE TAŞINDI
    const fetchMyCourses = async () => {
      if (currentUser) {
        setLoading(true);
        try {
          const data = await api.getUserCourses(currentUser.id); // Veya currentUser._id
          setMyCourses(data);
        } catch (error) {
          console.error("Kurslarım yüklenemedi:", error);
        } finally {
          setLoading(false);
        }
      }
    };

    fetchMyCourses();
    
  }, [currentUser]); // <-- Artık fetchMyCourses'a bağımlı değil, çünkü o artık bu scope'un içinde.

  // ... (render kısmı aynı kalır)
  return (
    <div>
        {/* ... */}
    </div>
  );
}

export default MyCourses;
