import React, { useState, useEffect, useContext } from 'react';
import { api } from '../../services/api';
import { AuthContext } from '../../App';
import { useNavigate } from 'react-router-dom';

function CourseList() {
  const [courses, setCourses] = useState([]);
  const [loading, setLoading] = useState(true);
  const { currentUser, updateUser } = useContext(AuthContext); // updateUser'ı context'ten al
  const navigate = useNavigate();

  const fetchCourses = async () => {
    setLoading(true);
    try {
      const data = await api.getCourses();
      setCourses(data);
    } catch (error) {
      console.error("Kurslar yüklenemedi:", error);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchCourses();
  }, []);

  const handleEnroll = async (courseId) => {
    if (!currentUser) {
      alert("Kursa katılmak için giriş yapmalısınız.");
      navigate('/login');
      return;
    }
    try {
      // API'den güncellenmiş kullanıcı verisini al
      const updatedUser = await api.enrollInCourse(currentUser.id, courseId);
      // Context aracılığıyla tüm uygulamadaki kullanıcı verisini güncelle
      updateUser(updatedUser);
      alert("Kursa başarıyla katıldınız!");
    } catch (error) {
      alert(error.message);
    }
  };

  const handleDelete = async (courseId, courseTitle) => {
    if (window.confirm(`'${courseTitle}' adlı kursu silmek istediğinizden emin misiniz?`)) {
      try {
        await api.deleteCourse(courseId);
        alert(`'${courseTitle}' kursu başarıyla silindi.`);
        fetchCourses();
      } catch (error) {
        alert("Kurs silinirken bir hata oluştu.");
      }
    }
  };

  if (loading) return <p>Kurslar yükleniyor...</p>;

  return (
    <div>
      <h1>Tüm Kurslar</h1>
      <div className="course-list">
        {courses.map(course => {
          // Kullanıcının bu kursa zaten kayıtlı olup olmadığını kontrol et
          const isEnrolled = currentUser?.myCourses?.includes(course.id);

          return (
            <div key={course.id} className="course-card">
              <h3>{course.title}</h3>
              <p>{course.description}</p>
              <p className="instructor">Eğitmen: {course.instructor}</p>
              <div className="card-buttons">
                {currentUser && currentUser.role !== 'admin' && (
                  <button
                    className="btn btn-join"
                    onClick={() => handleEnroll(course.id)}
                    disabled={isEnrolled} // Eğer kayıtlıysa butonu devre dışı bırak
                  >
                    {isEnrolled ? 'Kayıtlısınız' : 'Kursa Katıl'}
                  </button>
                )}
                {currentUser && currentUser.role === 'admin' && (
                  <button
                    className="btn btn-delete"
                    onClick={() => handleDelete(course.id, course.title)}
                  >
                    Sil
                  </button>
                )}
              </div>
            </div>
          );
        })}
      </div>
    </div>
  );
}

export default CourseList;
