import "./Login.css";
import React, { useState } from 'react';
import { useNavigate, Link } from 'react-router-dom'; // Sayfa yönlendirmesi için
import axios from 'axios';

const LoginForm = () => {
  const navigate = useNavigate(); // Giriş başarılıysa yönlendirme yapacağız

  // State'ler: Formda girilen e-posta ve şifre tutulur
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState(null); // Hata varsa gösterelim

  const handleLogin = async (e) => {
    e.preventDefault(); // Sayfa yenilenmesin

    try {
      const response = await axios.post('http://localhost:8000/api/login', {
        email,
        password,
      });

      // Backend'den kullanıcı bilgisi döndüyse saklıyoruz
      localStorage.setItem('user', JSON.stringify(response.data.user));
      navigate('/home'); // Kurslar sayfasına yönlendir

    } catch (err) {
      console.error(err);
      setError('Giriş başarısız. Bilgileri kontrol et.');
    }
  };

  return (
    <div>
      <h2>Giriş Yap</h2>
      {error && <p style={{ color: 'red' }}>{error}</p>}

      <form onSubmit={handleLogin}>
        <input
          type="email"
          placeholder="E-posta"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          required
        /><br />

        <input
          type="password"
          placeholder="Şifre"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          required
        /><br />

        <button type="submit">Giriş Yap</button>
      </form>

      <p>Hesabın yok mu? <Link to="/register">Kayıt Ol</Link></p>
    </div>
  );
};

export default LoginForm;
