import React, { useState, useEffect, useContext } from 'react';
import { api } from '../../services/api';
import { AuthContext } from '../../App';

function CourseList() {
  const [courses, setCourses] = useState([]);
  const [loading, setLoading] = useState(true);
  const { currentUser } = useContext(AuthContext);

  useEffect(() => {
    fetchCourses();
  }, []);

  const fetchCourses = async () => {
    setLoading(true);
    const data = await api.getCourses();
    setCourses(data);
    setLoading(false);
  };
  
  const handleEnroll = async (courseId) => {
      if (!currentUser) {
        alert("Kursa katılmak için giriş yapmalısınız.");
        return;
      }
      try {
        await api.enrollInCourse(currentUser.id, courseId);
        alert("Kursa başarıyla katıldınız!");
        // Butonu devre dışı bırakmak için state'i güncellemek gerekebilir ama şimdilik alert yeterli.
        // Gerçek uygulamada, kullanıcı verisi güncellenip buton durumu değiştirilebilir.
      } catch (error) {
        alert(error.message);
      }
  };

  if (loading) return <p>Kurslar yükleniyor...</p>;

  return (
    <div>
      <h1>Tüm Kurslar</h1>
      <div className="course-list">
        {courses.map(course => (
          <div key={course.id} className="course-card">
            <h3>{course.title}</h3>
            <p>{course.description}</p>
            <p className="instructor">Eğitmen: {course.instructor}</p>
            <button 
              className="btn btn-join" 
              onClick={() => handleEnroll(course.id)}
              disabled={!currentUser}
            >
              Kursa Katıl
            </button>
          </div>
        ))}
      </div>
    </div>
  );
}

export default CourseList;
