import React, { useState, useEffect, useContext } from 'react';
import { api } from '../../services/api';
import { AuthContext } from '../../App';

function MyCourses() {
  const [myCourses, setMyCourses] = useState([]);
  const [loading, setLoading] = useState(true);
  const { currentUser } = useContext(AuthContext);

  useEffect(() => {
    if (currentUser) {
      const fetchMyCourses = async () => {
        setLoading(true);
        try {
          const data = await api.getUserCourses(currentUser.id);
          setMyCourses(data);
        } catch (error) {
          console.error(error);
        } finally {
          setLoading(false);
        }
      };
      fetchMyCourses();
    }
  }, [currentUser]);

  if (loading) return <p>Kurslarınız yükleniyor...</p>;

  return (
    <div>
      <h1>Katıldığım Kurslar</h1>
      <div className="my-courses">
        {myCourses.length > 0 ? (
          myCourses.map(course => (
            <div key={course.id} className="course-card">
              <h3>{course.title}</h3>
              <p>{course.description}</p>
              <p className="instructor">Eğitmen: {course.instructor}</p>
            </div>
          ))
        ) : (
          <p>Henüz hiçbir kursa katılmadınız.</p>
        )}
      </div>
    </div>
  );
}

export default MyCourses;
