import React, { useState } from "react";
import { useNavigate, Link } from "react-router-dom";
// axios ve AuthContext'e ihtiyacımız yok.
import "./Login.css"; // Stil dosyası Login ile aynı olabilir, tutarlılık için.

function RegisterForm() {
  const [name, setName] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [passwordConfirmation, setPasswordConfirmation] = useState(""); // Şifre onayı için
  const [error, setError] = useState("");
  const [loading, setLoading] = useState(false);
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError("");
    setLoading(true);

    // Şifrelerin eşleşip eşleşmediğini frontend'de de kontrol edelim
    if (password !== passwordConfirmation) {
      setError("Şifreler eşleşmiyor!");
      setLoading(false);
      return;
    }

    try {
      const response = await fetch(
        `${process.env.REACT_APP_API_URL}/auth/register`,
        {
          method: "POST",
          headers: {
            "Content-Type": "application/json",
            Accept: "application/json",
          },
          body: JSON.stringify({
            name,
            email,
            password,
            password_confirmation: passwordConfirmation, // Backend'e bu alanı da gönderiyoruz
          }),
        }
      );

      const data = await response.json();

      if (!response.ok) {
        // Backend'den gelen validasyon hatalarını daha güzel gösterelim
        const errorMessage = data.errors
          ? Object.values(data.errors).flat().join(" ")
          : "Kayıt başarısız oldu. Lütfen bilgileri kontrol edin.";
        throw new Error(errorMessage);
      }

      // Kayıt başarılıysa, backend bize zaten bir token döner.
      // Bu token'ı ve kullanıcıyı hemen localStorage'a kaydederek otomatik giriş yapmış oluyoruz.
      const { access_token, user } = data;
      localStorage.setItem("authToken", access_token);
      localStorage.setItem("user", JSON.stringify(user));

      // Kullanıcıyı doğrudan ana paneline yönlendiriyoruz.
      navigate("/dashboard");
    } catch (err) {
      setError(err.message || "Bir hata oluştu.");
      console.error("Kayıt hatası:", err);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="login-container">
      {" "}
      {/* login-container stilini kullanabiliriz */}
      <h2>Kayıt Ol</h2>
      <form onSubmit={handleSubmit}>
        {error && <p className="error-message">{error}</p>}
        <div className="form-group">
          <label htmlFor="name">İsim Soyisim</label>
          <input
            type="text"
            id="name"
            value={name}
            onChange={(e) => setName(e.target.value)}
            required
          />
        </div>
        <div className="form-group">
          <label htmlFor="email">E-posta</label>
          <input
            type="email"
            id="email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required
          />
        </div>
        <div className="form-group">
          <label htmlFor="password">Şifre</label>
          <input
            type="password"
            id="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
          />
        </div>
        {/* Şifre onayı alanı */}
        <div className="form-group">
          <label htmlFor="password_confirmation">Şifre Tekrar</label>
          <input
            type="password"
            id="password_confirmation"
            value={passwordConfirmation}
            onChange={(e) => setPasswordConfirmation(e.target.value)}
            required
          />
        </div>
        <button type="submit" className="btn" disabled={loading}>
          {loading ? "Kayıt Olunuyor..." : "Kayıt Ol"}
        </button>
      </form>
      <div className="form-footer">
        <p>
          Zaten bir hesabın var mı? <Link to="/login">Giriş Yap</Link>
        </p>
      </div>
    </div>
  );
}

export default RegisterForm;
