import React, { useState, useEffect, useContext } from 'react';
import { api } from '../../services/api';
import { AuthContext } from '../../App';

function CourseList() {
  const [courses, setCourses] = useState([]);
  const [loading, setLoading] = useState(true);
  const { currentUser } = useContext(AuthContext);

  // Kursları getiren fonksiyon
  const fetchCourses = async () => {
    setLoading(true);
    try {
      const data = await api.getCourses();
      setCourses(data);
    } catch (error) {
      console.error("Kurslar yüklenemedi:", error);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchCourses();
  }, []);

  // Kursa katılma fonksiyonu
  const handleEnroll = async (courseId) => {
    if (!currentUser) {
      alert("Kursa katılmak için giriş yapmalısınız.");
      return;
    }
    try {
      await api.enrollInCourse(currentUser.id, courseId);
      alert("Kursa başarıyla katıldınız!");
    } catch (error) {
      alert(error.message);
    }
  };

  // YENİ: Kurs silme fonksiyonu
  const handleDelete = async (courseId, courseTitle) => {
    // Kullanıcıdan onay al
    if (window.confirm(`'${courseTitle}' adlı kursu silmek istediğinizden emin misiniz? Bu işlem geri alınamaz.`)) {
      try {
        await api.deleteCourse(courseId);
        alert(`'${courseTitle}' kursu başarıyla silindi.`);
        // Listeyi güncellemek için kursları yeniden çek
        fetchCourses();
      } catch (error) {
        alert("Kurs silinirken bir hata oluştu.");
      }
    }
  };

  if (loading) return <p>Kurslar yükleniyor...</p>;

  return (
    <div>
      <h1>Tüm Kurslar</h1>
      <div className="course-list">
        {courses.map(course => (
          <div key={course.id} className="course-card">
            <h3>{course.title}</h3>
            <p>{course.description}</p>
            <p className="instructor">Eğitmen: {course.instructor}</p>
            <div className="card-buttons">
              <button
                className="btn btn-join"
                onClick={() => handleEnroll(course.id)}
                disabled={!currentUser || (currentUser && currentUser.myCourses && currentUser.myCourses.includes(course.id))}
              >
                Kursa Katıl
              </button>
              {/* YENİ: Sadece admin ise "Sil" butonunu göster */}
              {currentUser && currentUser.role === 'admin' && (
                <button
                  className="btn btn-delete"
                  onClick={() => handleDelete(course.id, course.title)}
                >
                  Sil
                </button>
              )}
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}

export default CourseList;
