import React, { useEffect, useState } from 'react';
import axios from 'axios';
import MyCourses from '../components/Courses/MyCourses';
import { useNavigate } from 'react-router-dom';

const Home = () => {
  const navigate = useNavigate();
  const [courses, setCourses] = useState([]);
  const [user, setUser] = useState(null);
  const [enrolledCourses, setEnrolledCourses] = useState([]);

  useEffect(() => {
    // Giriş yapan kullanıcı bilgilerini al
    const storedUser = JSON.parse(localStorage.getItem('user'));
    if (!storedUser) {
      navigate('/'); // Giriş yapılmadıysa login sayfasına gönder
    } else {
      setUser(storedUser);
      fetchCourses(); // Kursları getir
      fetchMyCourses(storedUser.id); // Kayıtlı kursları getir
    }
  }, []);

  const fetchCourses = async () => {
    try {
      const res = await axios.get('http://localhost:8000/api/courses');
      setCourses(res.data);
    } catch (err) {
      console.error('Kurslar alınamadı:', err);
    }
  };

  const fetchMyCourses = async (userId) => {
    try {
      const res = await axios.get(`http://localhost:8000/api/my-courses/${userId}`);
      setEnrolledCourses(res.data.map(course => course.id)); // ID listesi
    } catch (err) {
      console.error('Kayıtlı kurslar alınamadı:', err);
    }
  };

  const handleEnroll = async (courseId) => {
    try {
      await axios.post('http://localhost:8000/api/enroll', {
        user_id: user.id,
        course_id: courseId,
      });
      setEnrolledCourses([...enrolledCourses, courseId]);
    } catch (err) {
      console.error('Kayıt olunamadı:', err);
    }
  };

  const handleCancel = async (courseId) => {
    try {
      await axios.post('http://localhost:8000/api/cancel', {
        user_id: user.id,
        course_id: courseId,
      });
      setEnrolledCourses(enrolledCourses.filter(id => id !== courseId));
    } catch (err) {
      console.error('Kayıt iptal edilemedi:', err);
    }
  };

  const handleLogout = () => {
    localStorage.removeItem('user');
    navigate('/');
  };

  return (
    <div>
      <header style={{ display: 'flex', justifyContent: 'space-between', marginBottom: '20px' }}>
        <h2>Hoş geldin, {user?.name}</h2>
        <button onClick={handleLogout}>Çıkış Yap</button>
      </header>

      <h3>Aktif Kurslar</h3>
      <ul>
        {courses.map(course => (
          <li key={course.id} style={{ marginBottom: '10px' }}>
            <strong>{course.name}</strong>
            {enrolledCourses.includes(course.id) ? (
              <button onClick={() => handleCancel(course.id)} style={{ marginLeft: '10px' }}>
                Kaydı İptal Et
              </button>
            ) : (
              <button onClick={() => handleEnroll(course.id)} style={{ marginLeft: '10px' }}>
                Kaydol
              </button>
            )}
          </li>
        ))}
      </ul>
    </div>
  );
};
<MyCourses />
export default Home;
