import React, { useState, useEffect, useContext } from 'react';
import { api } from '../../services/api';
import { AuthContext } from '../../App';

function MyCourses() {
  const [myCourses, setMyCourses] = useState([]);
  const [loading, setLoading] = useState(true);
  const { currentUser, updateUser } = useContext(AuthContext); // updateUser'ı context'ten al

  const fetchMyCourses = async () => {
    if (currentUser) {
      setLoading(true);
      try {
        const data = await api.getUserCourses(currentUser.id);
        setMyCourses(data);
      } catch (error) {
        console.error(error);
      } finally {
        setLoading(false);
      }
    }
  };

  useEffect(() => {
    fetchMyCourses();
  }, [currentUser]); // currentUser değiştiğinde listeyi yenile

  const handleUnenroll = async (courseId, courseTitle) => {
    if (window.confirm(`'${courseTitle}' kursundan ayrılmak istediğinizden emin misiniz?`)) {
      try {
        // API'den güncellenmiş kullanıcı verisini al
        const updatedUser = await api.unenrollFromCourse(currentUser.id, courseId);
        // Context aracılığıyla tüm uygulamadaki kullanıcı verisini güncelle
        updateUser(updatedUser);
        alert(`'${courseTitle}' kursundan başarıyla ayrıldınız.`);
        // Listeyi yeniden çekmeye gerek kalmadı, çünkü useEffect [currentUser] ile tetiklenecek.
      } catch (error) {
        alert("Kurstan ayrılırken bir hata oluştu.");
      }
    }
  };

  if (loading) return <p>Kurslarınız yükleniyor...</p>;

  return (
    <div>
      <h1>Katıldığım Kurslar</h1>
      <div className="my-courses">
        {myCourses.length > 0 ? (
          myCourses.map(course => (
            <div key={course.id} className="course-card">
              <h3>{course.title}</h3>
              <p>{course.description}</p>
              <p className="instructor">Eğitmen: {course.instructor}</p>
              <div className="card-buttons">
                <button
                  className="btn btn-delete"
                  onClick={() => handleUnenroll(course.id, course.title)}
                >
                  Kurstan Ayrıl
                </button>
              </div>
            </div>
          ))
        ) : (
          <p>Henüz hiçbir kursa katılmadınız.</p>
        )}
      </div>
    </div>
  );
}

export default MyCourses;
