import React, { useContext } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { AuthContext } from './App'; // App.jsx'ten AuthContext'i import et

function Navbar() {
  // Bilgileri doğrudan context'ten al
  const { isAuthenticated, user, logout } = useContext(AuthContext);
  const navigate = useNavigate();

  const handleLogout = () => {
    logout(); // Context'teki logout fonksiyonunu çağır
    navigate('/');
  };

  return (
    <nav className="navbar">
      <Link to="/" className="navbar-brand">
        {/* ... logo ve başlık ... */}
      </Link>
      <ul className="nav-links">
        <li><Link to="/">Ana Sayfa</Link></li>
        <li><Link to="/courses">Kurslar</Link></li>
        
        {isAuthenticated ? (
          <>
            <li><Link to="/my-courses">Kurslarım</Link></li>
            {user?.is_admin && (
              <li><Link to="/admin">Admin Paneli</Link></li>
            )}
            <li><button onClick={handleLogout}>Çıkış Yap</button></li>
          </>
        ) : (
          <>
            <li><Link to="/login">Giriş Yap</Link></li>
            <li><Link to="/register">Kayıt Ol</Link></li>
          </>
        )}
      </ul>
    </nav>
  );
}

export default Navbar;
