  return (
    <BrowserRouter>
      <div className="App">
        <Navbar />

        <main className="container">
          <Routes>
            {/* ... Diğer Rotalar ... */}

            {/* Bu rotanın çalışması için 'AdminPanel' veya 'AdminPage' import edilmiş olmalı */}
