import React, { useState } from 'react';
import { api } from '../../services/api';

function CourseForm({ onCourseAdded }) {
  const [title, setTitle] = useState('');
  const [description, setDescription] = useState('');
  const [instructor, setInstructor] = useState('');
  const [message, setMessage] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!title || !description || !instructor) {
      setMessage('Lütfen tüm alanları doldurun.');
      return;
    }
    try {
      const newCourse = await api.addCourse({ title, description, instructor });
      setMessage(`'${newCourse.title}' başarıyla eklendi!`);
      setTitle('');
      setDescription('');
      setInstructor('');
      if(onCourseAdded) onCourseAdded(); // AdminPanel'i haberdar et
    } catch (error) {
      setMessage('Kurs eklenirken bir hata oluştu.');
    }
  };

  return (
    <form onSubmit={handleSubmit} className="form-container">
      {message && <p>{message}</p>}
      <div className="form-group">
        <label htmlFor="title">Kurs Başlığı</label>
        <input
          type="text"
          id="title"
          value={title}
          onChange={(e) => setTitle(e.target.value)}
        />
      </div>
      <div className="form-group">
        <label htmlFor="description">Açıklama</label>
        <input
          type="text"
          id="description"
          value={description}
          onChange={(e) => setDescription(e.target.value)}
        />
      </div>
      <div className="form-group">
        <label htmlFor="instructor">Eğitmen</label>
        <input
          type="text"
          id="instructor"
          value={instructor}
          onChange={(e) => setInstructor(e.target.value)}
        />
      </div>
      <button type="submit" className="btn">Kurs Ekle</button>
    </form>
  );
}

export default CourseForm;
